import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 20px;
`;

const Input = styled.input`
  padding: 10px;
  margin-bottom: 20px;
  font-size: 18px;
  border-radius: 5px;
  border: none;
  box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.1);
`;

const Message = styled.div`
  background-color: #f8f8f8;
  border-radius: 5px;
  padding: 10px;
  margin-bottom: 10px;
  max-width: 500px;
`;

const ShortPolling = () => {
  const [messages, setMessages] = useState({ data: [] });
  const [inputValue, setInputValue] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch('http://localhost:5000/short/notification');
      const data = await response.json();
      if(data.length > 0){
        setMessages({ data:data.data });
        // console.log({ data:data.data });
      }

    };

    fetchData();
  }, []);

  const handleMessageSend = async () => {
    const response = await fetch('http://localhost:5000/short/notification', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ message: inputValue }),
    });

    const data = await response.json();

    if (data.success) {
        setMessages({ data: [...messages.data, data] });
      setInputValue('');
    }
  };

  return (
    <Container>
      <h1>Short Polling Example</h1>
      <Input
        type="text"
        placeholder="Enter a message"
        value={inputValue}
        onChange={(event) => setInputValue(event.target.value)}
      />
      <button onClick={handleMessageSend}>Send</button>
      {messages.data.data.data.map((message) => (
        <Message key={message._id}>{message.content}</Message>
        ))}
    </Container>
  );
};

export default ShortPolling;
